dataSample.sql:
CREATE TABLE IF NOT EXISTS `PREFIX_appagebuilder` ( `id_appagebuilder` int(11) NOT NULL AUTO_INCREMENT, `id_appagebuilder_positions` int(11) NOT NULL, `hook_name` varchar(255) DEFAULT NULL, PRIMARY KEY (`id_appagebuilder`) ) ENGINE=ENGINE_TYPE AUTO_INCREMENT=31 DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_appagebuilder_lang` ( `id_appagebuilder` int(11) NOT NULL AUTO_INCREMENT, `id_lang` int(10) unsigned NOT NULL, `params` text, PRIMARY KEY (`id_appagebuilder`,`id_lang`) ) ENGINE=ENGINE_TYPE AUTO_INCREMENT=31 DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_appagebuilder_positions` ( `id_appagebuilder_positions` int(11) NOT NULL AUTO_INCREMENT, `name` varchar(255) NOT NULL, `position` varchar(255) NOT NULL, `position_key` varchar(255) NOT NULL, `params` text, PRIMARY KEY (`id_appagebuilder_positions`) ) ENGINE=ENGINE_TYPE AUTO_INCREMENT=10 DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_appagebuilder_positions_shop` ( `id_appagebuilder_positions` int(11) NOT NULL AUTO_INCREMENT, `id_shop` int(10) unsigned NOT NULL, PRIMARY KEY (`id_appagebuilder_positions`,`id_shop`) ) ENGINE=ENGINE_TYPE AUTO_INCREMENT=10 DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_appagebuilder_products` ( `id_appagebuilder_products` int(11) NOT NULL AUTO_INCREMENT, `plist_key` varchar(255) DEFAULT NULL, `name` varchar(255) DEFAULT NULL, `class` varchar(255) DEFAULT NULL, `params` text, `type` tinyint(1) DEFAULT NULL, `active` tinyint(1) DEFAULT NULL, PRIMARY KEY (`id_appagebuilder_products`) ) ENGINE=ENGINE_TYPE AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_appagebuilder_products_shop` ( `id_appagebuilder_products` int(11) NOT NULL AUTO_INCREMENT, `id_shop` int(10) unsigned NOT NULL, `active` tinyint(1) DEFAULT NULL, PRIMARY KEY (`id_appagebuilder_products`,`id_shop`) ) ENGINE=ENGINE_TYPE AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_appagebuilder_profiles` ( `id_appagebuilder_profiles` int(11) NOT NULL AUTO_INCREMENT, `name` varchar(255) DEFAULT NULL, `profile_key` varchar(255) DEFAULT NULL, `page` varchar(255) DEFAULT NULL, `params` text, `header` int(11) unsigned NOT NULL, `content` int(11) unsigned NOT NULL, `footer` int(11) unsigned NOT NULL, `product` int(11) unsigned NOT NULL, `active` tinyint(1) DEFAULT NULL, PRIMARY KEY (`id_appagebuilder_profiles`) ) ENGINE=ENGINE_TYPE AUTO_INCREMENT=7 DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_appagebuilder_profiles_shop` ( `id_appagebuilder_profiles` int(11) NOT NULL AUTO_INCREMENT, `id_shop` int(10) unsigned NOT NULL, `active` tinyint(1) DEFAULT NULL, PRIMARY KEY (`id_appagebuilder_profiles`,`id_shop`) ) ENGINE=ENGINE_TYPE AUTO_INCREMENT=7 DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_appagebuilder_shop` ( `id_appagebuilder` int(11) NOT NULL AUTO_INCREMENT, `id_shop` int(10) unsigned NOT NULL, PRIMARY KEY (`id_appagebuilder`,`id_shop`) ) ENGINE=ENGINE_TYPE AUTO_INCREMENT=31 DEFAULT CHARSET=utf8;
